import React, { useState } from 'react';

function App() {
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [data, setData] = useState();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    name === 'team1' ? setTeam1(value) : setTeam2(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    if (team1 && team2) {
      fetch(`http://127.0.0.1:5000/predict?team1=${team1}&team2=${team2}`)
        .then(res => res.json())
        .then(data => {
          setData(data);
          console.log(data);
        })
        .catch(error => console.error('Error fetching data:', error));
    }
  };

  const handleReset = () => {
    setTeam1('');
    setTeam2('');
    setData(null);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Team 1:
          <input type="text" name="team1" value={team1} onChange={handleInputChange} />
        </label>
        <br />
        <label>
          Team 2:
          <input type="text" name="team2" value={team2} onChange={handleInputChange} />
        </label>
        <br />
        <button type="submit">Predict</button>
        <button type="button" onClick={handleReset}>Reset</button>
      </form>

      {data && (
        <p>Prediction: {data}</p>
      )}
    </div>
  );
}

export default App;
