import React, { useState, useEffect } from 'react';

function App() {
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [data, setData] = useState();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    name === 'team1' ? setTeam1(value) : setTeam2(value);
  };

  const handleSubmit = () => {
    fetch(`http://127.0.0.1:5000/predict?team1=${team1}&team2=${team2}`)
      .then(res => res.json())
      .then(data => {
        setData(data);
        console.log(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  };

  useEffect(() => {
    // Fetch data when the user submits the form
    if (team1 && team2) {
      fetch(`http://127.0.0.1:5000/predict?team1=${team1}&team2=${team2}`)
        .then(res => res.json())
        .then(data => {
          setData(data);
          console.log(data);
        })
        .catch(error => console.error('Error fetching data:', error));
    }
  }, [team1, team2]);


  return (
    <div>
      <label>
        Team 1:
        <input type="text" name="team1" value={team1} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Team 2:
        <input type="text" name="team2" value={team2} onChange={handleInputChange} />
      </label>
      <br />
      <button onClick={handleSubmit}>Predict</button>

      {data && (
        <p>Prediction: {data}</p>
      )}
    </div>
  );
}

export default App;
