import React, { useState } from 'react';
import './index.css'; // Import your main CSS file (generated by Tailwind CSS)
import nba_background from "./client/public/nba_background"

const nbaTeams = [
  'Atlanta Hawks', 'Boston Celtics', 'Brooklyn Nets', 'Charlotte Hornets', 'Chicago Bulls',
  'Cleveland Cavaliers', 'Dallas Mavericks', 'Denver Nuggets', 'Detroit Pistons', 'Golden State Warriors',
  'Houston Rockets', 'Indiana Pacers', 'LA Clippers', 'Los Angeles Lakers', 'Memphis Grizzlies',
  'Miami Heat', 'Milwaukee Bucks', 'Minnesota Timberwolves', 'New Orleans Pelicans', 'New York Knicks',
  'Oklahoma City Thunder', 'Orlando Magic', 'Philadelphia 76ers', 'Phoenix Suns', 'Portland Trail Blazers',
  'Sacramento Kings', 'San Antonio Spurs', 'Toronto Raptors', 'Utah Jazz', 'Washington Wizards'
];

function App() {
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [data, setData] = useState();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    name === 'team1' ? setTeam1(value) : setTeam2(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (team1 && team2) {
      fetch(`http://127.0.0.1:5000/predict?team1=${team1}&team2=${team2}`)
        .then(res => res.json())
        .then(data => {
          setData(data);
          console.log(data);
        })
        .catch(error => console.error('Error fetching data:', error));
    }
  };

  const handleReset = () => {
    setTeam1('');
    setTeam2('');
    setData(null);
  };

  return (
    <div 
      className="bg-gradient-to-b from-gray-900 to-gray-700 text-white min-h-screen flex items-center justify-center"
      style={{ backgroundImage: `url(${nba_background})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
    >
      <div className="bg-gray-800 p-8 rounded-md shadow-md w-full max-w-md">
        <h1 className="text-3xl font-bold mb-8 text-center">
          Twitter API-Powered Sentiment Analysis for NBA Game Prediction
        </h1>

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="team1" className="block text-sm font-medium">
              Team 1:
            </label>
            <select
              id="team1"
              name="team1"
              value={team1}
              onChange={handleInputChange}
              className="mt-1 p-2 border rounded-md w-full bg-gray-700 text-white"
            >
              <option value="" disabled>Select Team 1</option>
              {nbaTeams.map((team, index) => (
                <option key={index} value={team}>{team}</option>
              ))}
            </select>
          </div>
          <div className="mb-4">
            <label htmlFor="team2" className="block text-sm font-medium">
              Team 2:
            </label>
            <select
              id="team2"
              name="team2"
              value={team2}
              onChange={handleInputChange}
              className="mt-1 p-2 border rounded-md w-full bg-gray-700 text-white"
            >
              <option value="" disabled>Select Team 2</option>
              {nbaTeams.map((team, index) => (
                <option key={index} value={team}>{team}</option>
              ))}
            </select>
          </div>
          <div className="flex space-x-4">
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
            >
              Predict
            </button>
            <button
              type="button"
              onClick={handleReset}
              className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
            >
              Reset
            </button>
          </div>
        </form>

        {data && (
          <p className="mt-4 text-green-400 font-semibold">Prediction: {data}</p>
        )}
      </div>
    </div>
  );
}

export default App;
