import React, { useState } from 'react';
import './index.css'; // Import your main CSS file (generated by Tailwind CSS)

function App() {
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [data, setData] = useState();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    name === 'team1' ? setTeam1(value) : setTeam2(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (team1 && team2) {
      fetch(`http://127.0.0.1:5000/predict?team1=${team1}&team2=${team2}`)
        .then(res => res.json())
        .then(data => {
          setData(data);
          console.log(data);
        })
        .catch(error => console.error('Error fetching data:', error));
    }
  };

  const handleReset = () => {
    setTeam1('');
    setTeam2('');
    setData(null);
  };

  return (
    <div className="container mx-auto p-8 bg-gradient-to-b from-gray-900 to-gray-700 text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">
        Twitter API-Powered Sentiment Analysis for NBA Game Prediction
      </h1>

      <form onSubmit={handleSubmit} className="bg-gray-800 p-6 rounded-md shadow-md">
        <div className="mb-4">
          <label htmlFor="team1" className="block text-sm font-medium">
            Team 1:
          </label>
          <input
            type="text"
            id="team1"
            name="team1"
            value={team1}
            onChange={handleInputChange}
            className="mt-1 p-2 border rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="team2" className="block text-sm font-medium">
            Team 2:
          </label>
          <input
            type="text"
            id="team2"
            name="team2"
            value={team2}
            onChange={handleInputChange}
            className="mt-1 p-2 border rounded-md w-full"
          />
        </div>
        <div className="flex space-x-4">
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
          >
            Predict
          </button>
          <button
            type="button"
            onClick={handleReset}
            className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
          >
            Reset
          </button>
        </div>
      </form>

      {data && (
        <p className="mt-4 text-green-400 font-semibold">Prediction: {data}</p>
      )}
    </div>
  );
}

export default App;
